import { useParams } from "react-router-dom";
import { useFetch } from "react";

const BlogsDetails = () => {
  const {
    data: blogs,
    error,
    isPeanding,
  } = useFetch("http://localhost:8000/blogs/" + id);
  const { id } = useParams();
  return (
    <div className="blogs-details">
      {isPeanding && <div> Loading...</div>}
      {error && <div>{error}</div>}
      {blogs && (
        <article>
          <h2>{blogs.title}</h2>
          <p> written by {blogs.author}</p>
          <div> {blogs.body}</div>
        </article>
      )}
      <h2> Blogs Details - {id} </h2>
    </div>
  );
};

export default BlogsDetails;
